//import { popup } from "../redux/actions/actions";

export const rateHotel = (hotel,openPopup)=>{

    return ()=>{
        localStorage.setItem("hotel",JSON.stringify(hotel))
        openPopup(true)
    }
}

export const closePop = (actn)=>()=>actn(false);
