import React from "react";

export const ShowTotalComponent =({total,name})=>{

    const mystyle =()=>({
      color: "white",
      backgroundColor: "red",
      marginLeft:"5px",
      paddingLeft:"2px",
      padding: "1px",
      width:`${total}px`,
      fontSize:"10pt",
      height:"auto"
    });

    return(
        <div className="hotel-info">
            <p>{name}</p> 
            <p style={mystyle()}>
                {(total)}
            </p>
        </div>
    );
}
