import actionNames from "../actions/actionTypes"

 const hotel = (state={},action)=>{
    switch (action.type) {
        case actionNames.SUCCESS_HOTELS:
            return {
            id:state.id,
            name: state.name,
            stars: state.stars,
            location: state.location,
            services:state.services
            }
        default:
            return state;
    }
}

export const hotels = (state=[],action)=>{
    switch (action.type) {
        case actionNames.SUCCESS_HOTELS:
            const {hotels}=action;
            return hotels.map(htel=>hotel(htel,action));
        default:
            return state;
    }

}
const service = (state={},action)=>{
    switch (action.type) {
        case actionNames.SUCCESS_SERVICES:
            return{
                id:state.id,
                name:state.name
            }
        default:
            return state;
    }
}
export const services = (state=[],action)=>{
    switch (action.type) {
        case action.SUCCESS_SERVICES:
            console.log(action);
            return state.map((servc)=>service(state,servc))
        default:
            return state;
    }
}

 const vote = (state={},action)=>{
    switch (action.type) {
        case actionNames.SUCCESS_VOTE:
            return {
                id:action.id,
                hotelId: state.hotelId,
                serviceId: state.serviceId,
                votes: state.vote,
                email: state.email,
                createdAt: state.createdAt,
                 updatedAt: state.updatedAt,
                 service: state.service
            }
        default:
            return state;
    }
}

export const votes = (state=[],action)=>{
    switch (action.type) {
        case actionNames.SUCCESS_VOTE:
            return state.map((rate)=>vote(action,rate))
        default:
            return state;
    }
}
