    const {email_verified}=userReducer
    const [isLoggedIn,setLogin]=useState(email_verified);

    useEffect(()=>{
       setLogin
        console.log(email_verified)
    },[state])



    return (
        <div className='hotels'>
                {hotels.map((hotel)=>{
                    return <div key={hotel.id} className='hotel'>
                            <p>{hotel.name}</p>
                            <RatedServices services={hotel.services}/>
                              {(false)?<button className='votebtn' onClick={rateHotel(hotel,openPopup)} >How you treated?</button>:""}
                        </div>   



                })}
        </div>
    )
}
