import React, { useEffect } from "react";

export const AuthComponent = ({login,logout,state})=>{

    const {userReducer}=state
    const {given_name,picture,email_verified}=userReducer
    useEffect(()=>{
        login()
    },[login])

    const handleOnLogout =()=>{
        logout()
    };

    return(
        <div className="auth">
            {given_name}
            <img src={`${picture}`} height="100" width="100" alt={given_name}/>
            <button className="authbtn"><a href="http://localhost:5000/api/auth/google">Login with google</a></button>
            {(!email_verified)?<button className="authbtn" onClick={handleOnLogout}>Loout</button>:""}
        </div>
    );
}
