export const ShowTotalComponent =({services,total})=>{

    const mystyle =()=>({
      color: "white",
      backgroundColor: "red",
      marginLeft:"5px",
      paddingLeft:"2px",
      padding: "10px",
      width:`${total}px`,
    });
;

    return(
        <div className="hotel-info">
            <p style={mystyle()}>
                {(total)}
            </p>
        </div>
    );
}
