import {hotels,services} from './reducers';
import createSagaMiddleware from '@redux-saga/core'
import {combineReducers,applyMiddleware,createStore} from 'redux';
import rootSaga from '../saga'
import {fetch_hotels} from '../actions/actions';

const reducer = combineReducers({hotels,services});
const sagaMiddleware = createSagaMiddleware();
const store = createStore(hotels,applyMiddleware(sagaMiddleware));
sagaMiddleware.run(rootSaga);
store.dispatch(fetch_hotels("http://localhost:5000/api/hotel"));

export default store;
