//import { popup } from "../redux/actions/actions";

export const saveVote = (vote)=>{
    return ()=>console.log(vote)
}

export const closePop = (actn)=>()=>actn(false);

export const selectServices = (service,boolValue)=>{
    let services=[{}];
    if(!boolValue){
        let newServ = [...services]
        newServ.filter((serv)=>serv.id===service.id);
        return newServ
    }

    return ()=>console.log(services.concat(),boolValue)

}
