import {hotels,badservices,popup,ratedService} from './reducers';
import createSagaMiddleware from '@redux-saga/core'
import {applyMiddleware,createStore,combineReducers} from 'redux';
import rootSaga from '../saga'
import {fetch_hotels,fetch_services,authenticate} from '../actions/actions';
import {urls} from '../../helper/urls';

const reducer = combineReducers({hotels,badservices,popup,ratedService});
const sagaMiddleware = createSagaMiddleware();
const store = createStore(reducer,applyMiddleware(sagaMiddleware));
sagaMiddleware.run(rootSaga);
store.dispatch(fetch_hotels(urls.hotels));
store.dispatch(fetch_services(urls.services));
store.dispatch(authenticate(urls.auth))

export default store;
