import React, { useEffect, useState } from "react";
import { closePop, selectServices } from "../../helper/events";
import { badservices } from "../../redux/reducers/reducers";
import './popup.css';

export const VoteComponent = ({state,closePopup})=>{


 const {popup,badservices} = state;
    useEffect(()=>{
     const [checkedBoxes,setChecks]=useState(new Array(3).fill(false));
    },[badservices])
        const handleOnchecked=(pos)=>{
            const updateChecks=checkedBoxes.map((item,index)=>index===pos?!item:item)
            setChecks(updateChecks)
    }
    return (popup)?<div className="popup">
            <div className="popup-content">
                <div className="bad-service-list">
               {badservices.map((badservice)=>{
                   return(
                       <div key={badservice.id} className="bad-service">

                               {badservice.name}
                               <input type="checkbox" onChange={()=>handleOnchecked(badservice.id)} name={badservice.id} onClick={selectServices(badservice,checkedBoxes)}/>
                       </div>
                   );
               })}
               </div>
          <button onClick={closePop(closePopup)}>save</button>
            </div>
        </div>:""

}
