import {RatedServiceName} from './ratedServiceName';
export const RatedServices = ({services=[],user_email,isLoggedIn})=>{

      const handleOnBtnClick =(hotel)=>{
        const rateHotel = {...hotel};
        localStorage.setItem("hotel",JSON.stringify(rateHotel));
        openPopup(true)
    }
    return(
            <div className='voted-services'>
                {services.map((vote)=>{
                return(
                    <div>
                    <div  key={vote.id} className='service'>
                        <RatedServiceName service={vote.service}/>
                        <p>{`(${vote.votes})`}</p>
                    </div>
                     {(isLoggedIn||user_email!==vote.email )?<button className='votebtn' onClick={()=>handleOnBtnClick(hotel)} >How you treated?</button>:""}
                     </div>
                )
            })}</div>

    );
}
