import React from 'react';
import { rateHotel } from '../../helper/events';
import './css/hotel.css';
import {RatedServices} from './ratedServices/ratedServices';

export const HotelComponent = ({state,openPopup,fetchHotels})=>{
    const {userReducer,hotels}=state; 
    const {email_verified}=userReducer


    return (
        <div className='hotels'>
                {hotels.map((hotel)=>{
                    return <div key={hotel.id} className='hotel'>
                            <p>{hotel.name}</p>
                            <RatedServices services={hotel.services}/>
                              {(email_verified)?<button className='votebtn' onClick={rateHotel(hotel,openPopup)} >How you treated?</button>:""}
                        </div>   



                })}
        </div>
    )
}
