import {call,put,takeEvery} from 'redux-saga/effects'
import { success_fetch_hotels, success_fetch_services } from './actions/actions';
import actionNames from './actions/actionTypes';
import { hotels } from './reducers/reducers';
import { getData } from './sagaApi/apiCall'

function* fetchHotels(action){
    const hotles = yield call(getData,action.url);
    const services = yield call(getData,"http://localhost:5000/api/hotel");
    console.log(hotels,services)
    yield put(success_fetch_hotels(hotles));
    yield put(success_fetch_services(services));
}

// function* vote(action){
//     const newVote = yield call(createVote,action.newVote);
//     console.log(newVote);
// }

function* watcher(){
    yield takeEvery(actionNames.FETCH_HOTELS,fetchHotels)
}

export default watcher;
