import {call,takeEvery} from 'redux-saga/effects'
//import { success_fetch_hotels, success_fetch_services } from './actions/actions';
import { getData } from './sagaApi/apiCall'

function* fetchHotels(action){
    const hotles = yield call(getData,action.url)
    console.log(hotles);
}

// function* fetchServices(action){
//     const services = yield call(getData,action.url);
//     console.log(services);
// }

// function* vote(action){
//     const newVote = yield call(createVote,action.newVote);
//     console.log(newVote);
// }

function* watcher(){
    yield takeEvery('FETCH_HOTELS',fetchHotels)
}

export default watcher;
