import {services} from './reducers';
import createSagaMiddleware from '@redux-saga/core'
import {applyMiddleware,createStore} from 'redux';
import rootSaga from '../saga'
import {fetch_services} from '../actions/actions';

//const reducer = combineReducers({hotels,services});
const sagaMiddleware = createSagaMiddleware();
const store = createStore(services,applyMiddleware(sagaMiddleware));
sagaMiddleware.run(rootSaga);
store.dispatch(fetch_services("http://localhost:5000/api/service"))
//store.dispatch(fetch_hotels("http://localhost:5000/api/hotel"));

export default store;
